<?php$walk = $argv[1] ?? null;if (is_null($walk)) {    echo "Please walk direction.\n";    exit(0);}preg_match_all('/(R|L|W\d*)/', $walk, $walks);if (count($walks[0]) === 0) {    echo "Walk direction incorrect.\n";    exit(0);}$positions = [0, 0];$directions = ['North', 'East', 'South', 'West'];$direction = 0;foreach ($walks[0] as $walk) {    preg_match('/(R|L|W)(\d*)/', $walk, $move);    $amount = intval($move[2] ?: 1);    for ($i = 0; $i < $amount; $i++) {        switch ($move[1]) {            case 'L':                $direction--;                if ($direction < 0) $direction = count($directions) - 1;                break;            case 'R':                $direction++;                if ($direction >= count($directions)) $direction = 0;                break;            case 'W':                if (in_array($direction, [0, 1])) {                    $positions[$direction]++;                } else {                    $positions[$direction - 2]--;                }                break;        }    }}echo "X: {$positions[1]} Y: {$positions[0]} Direction: {$directions[$direction]}\n";exit(0);